import React, { useEffect, useState } from 'react';
import api from '../services/api';

const emptyForm = {
    firstName: '', lastName: '', email: '', mobile: '', country: '', state: '', city: '', pincode: '', address: '', gender: ''
};

const EmployeeForm = ({ editing, onSaved, onCancel }) => {
    const [form, setForm] = useState(emptyForm);
    const [errors, setErrors] = useState([]);

    const data = [
        {
            id:123,
            name:"ansh"
        },
        {
            id:456,
            name:"ansh"
        },
        {
            id:123,
            name:"ansh"
        }
    ]

    useEffect(() => {
        if (editing) setForm(editing);
        else setForm(emptyForm);
    }, [editing]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm(prev => ({ ...prev, [name]: value }));
    };

    const validate = () => {
        const e = [];
        if (!form.firstName || form.firstName.trim().length < 2) e.push('First name min 2 chars');
        if (!form.lastName || form.lastName.trim().length < 2) e.push('Last name min 2 chars');
        if (!form.email || !/^\S+@\S+\.\S+$/.test(form.email)) e.push('Valid email required');
        if (!form.mobile || !/^\d{10}$/.test(form.mobile)) e.push('Mobile must be 10 digits');
        if (!form.country) e.push('Country required');
        if (!form.state) e.push('State required');
        if (!form.city) e.push('City required');
        if (!form.pincode || !/^\d{6}$/.test(form.pincode)) e.push('Pincode must be 6 digits');
        if (!form.address || form.address.trim().length < 10) e.push('Address min 10 chars');
        if (!form.gender) e.push('Gender required');
        setErrors(e);
        return e.length === 0;
    };

    const handleSubmit = async (ev) => {
        ev.preventDefault();
        if (!validate()) return;
        try {
            if (editing && editing._id) {
                await api.put(`/${editing._id}`, form);
                alert('Employee updated');
            } else {
                await api.post('/', form);
                alert('Employee created');
            }
            setForm(emptyForm);
            onSaved && onSaved();
        } catch (err) {
            const msg = err?.response?.data?.message || err.message;
            setErrors([msg]);
        }
    };


    return (
        <div className="card p-3 mb-3">
            <h5>{editing ? 'Edit Employee' : 'Register Employee'}</h5>
            {errors.length > 0 && <div className="alert alert-danger"><ul className="mb-0">{errors.map((e, i) => <li key={i}>{e}</li>)}</ul></div>}
            <form onSubmit={handleSubmit}>
                <div className="row">
                    <div className="col-6 mb-2">
                        <label>First Name</label>
                        <input name="firstName" value={form.firstName} onChange={handleChange} className="form-control" />
                    </div>
                    <div className="col-6 mb-2">
                        <label>Last Name</label>
                        <input name="lastName" value={form.lastName} onChange={handleChange} className="form-control" />
                    </div>
                </div>
                <div className="mb-2">
                    <label>Email</label>
                    <input name="email" value={form.email} onChange={handleChange} className="form-control" />
                </div>
                <div className="mb-2">
                    <label>Mobile</label>
                    <input name="mobile" value={form.mobile} onChange={handleChange} className="form-control" />
                </div>
                <div className="row">
                    <div className="col-4 mb-2">
                        <label>Country</label>
                        <input name="country" value={form.country} onChange={handleChange} className="form-control" />
                    </div>
                    <div className="col-4 mb-2">
                        <label>State</label>
                        <input name="state" value={form.state} onChange={handleChange} className="form-control" />
                    </div>
                    <div className="col-4 mb-2">
                        <label>City</label>
                        <input name="city" value={form.city} onChange={handleChange} className="form-control" />
                    </div>
                </div>
                <div className="mb-2">
                    <label>Pincode</label>
                    <input name="pincode" value={form.pincode} onChange={handleChange} className="form-control" />
                </div>
                <div className="mb-2">
                    <label>Address</label>
                    <textarea name="address" value={form.address} onChange={handleChange} className="form-control" rows="2" />
                </div>
                <div className="mb-2">
                    <label>Gender</label>
                    <div></div>
                    {
                        ['Male', 'Female', 'Other'].map(g => (
                            <div className="form-check-inlinek from-check-inline" key={g}>
                                <input className="form-check-input" type="radio" name="gender" value={g} checked={form.gender === g} onChange={handleChange}></input>
                                <label className="from-check-label">{g}</label>
                            </div>
                        ))
                    }
                </div>
                <div className="d-flex gap-2">
                    <button className="btn btn-primary" type="submit">{editing ? 'Update' : 'Save'}</button>
                    {editing && <button type="button" className="btn btn-secondary" onClick={() => { setForm(emptyForm); onCancel && onCancel(); }}>Cancel</button>}
                </div>
            </form>
        </div>
    );
};   

export default EmployeeForm;
