import React, { useEffect, useState } from 'react';
import api from '../services/api';

// Initial empty form state
const emptyForm = {
  firstName: '',
  lastName: '',
  email: '',
  mobile: '',
  country: '',
  state: '',
  city: '',
  pincode: '',
  address: '',
  gender: '',
};

// Utility: Allow only alphabets and spaces
function allow_alphabets(element) {
  let textInput = element.value;
  textInput = textInput.replace(/[^A-Za-z ]/g, ""); // Remove non-alphabet characters
  element.value = textInput;
}

const EmployeeForm = ({ editing, onSaved, onCancel }) => {
  const [form, setForm] = useState(emptyForm);
  const [errors, setErrors] = useState({});

  // Reset form when `editing` prop changes
  useEffect(() => {
    if (editing) {
      setForm(editing);
    } else {
      setForm(emptyForm);
    }
  }, [editing]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;

    // Optionally sanitize input for name fields
    if (name === 'firstName' || name === 'lastName') {
      const input = e.target;
      setTimeout(() => allow_alphabets(input), 1); // Delay to capture value before mutation
    }

    setForm((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: '' })); // Clear error on change
  };

  // Full validation logic
  const validateForm = () => {
    const newErrors = {};

    if (!form.firstName || form.firstName.trim().length < 2) {
      newErrors.firstName = 'First name must be at least 2 characters';
    }

    if (!form.lastName || form.lastName.trim().length < 2) {
      newErrors.lastName = 'Last name must be at least 2 characters';
    }

    if (!form.email) {
      newErrors.email = 'Email is required';
    } else if (!/^\S+@\S+\.\S+$/.test(form.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!form.mobile) {
      newErrors.mobile = 'Mobile number is required';
    } else if (!/^\d{10}$/.test(form.mobile)) {
      newErrors.mobile = 'Mobile number must be 10 digits';
    }

    if (!form.country) newErrors.country = 'Country is required';
    if (!form.state) newErrors.state = 'State is required';
    if (!form.city) newErrors.city = 'City is required';
    if (!form.pincode) newErrors.pincode = 'Pincode is required';
    else if (!/^\d{6}$/.test(form.pincode)) newErrors.pincode = 'Pincode must be 6 digits';

    if (!form.address) newErrors.address = 'Address is required';
    if (!form.gender) newErrors.gender = 'Gender is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      if (editing?.id) {
        // Update existing employee
        await api.put(`/employees/${editing.id}`, form);
      } else {
        // Create new employee
        await api.post('/employees', form);
      }
      onSaved(); // Notify parent component
    } catch (err) {
      console.error('Error saving employee:', err);
      // Optionally set a global error or use toast notification
    }
  };

  return (
    <form onSubmit={handleSubmit} className="employee-form">
      <div className="form-row">
        <div className="form-group">
          <label>First Name</label>
          <input
            type="text"
            name="firstName"
            value={form.firstName}
            onChange={handleChange}
            onInput={(e) => {
              if (e.target.name === 'firstName') allow_alphabets(e.target);
            }}
            className={errors.firstName ? 'error' : ''}
          />
          {errors.firstName && <span className="error-text">{errors.firstName}</span>}
        </div>

        <div className="form-group">
          <label>Last Name</label>
          <input
            type="text"
            name="lastName"
            value={form.lastName}
            onChange={handleChange}
            onInput={(e) => {
              if (e.target.name === 'lastName') allow_alphabets(e.target);
            }}
            className={errors.lastName ? 'error' : ''}
          />
          {errors.lastName && <span className="error-text">{errors.lastName}</span>}
        </div>
      </div>

      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={form.email}
          onChange={handleChange}
          className={errors.email ? 'error' : ''}
        />
        {errors.email && <span className="error-text">{errors.email}</span>}
      </div>

      <div className="form-group">
        <label>Mobile</label>
        <input
          type="text"
          name="mobile"
          value={form.mobile}
          onChange={handleChange}
          className={errors.mobile ? 'error' : ''}
          maxLength="10"
        />
        {errors.mobile && <span className="error-text">{errors.mobile}</span>}
      </div>

      <div className="form-row">
        <div className="form-group">
          <label>Country</label>
          <input
            type="text"
            name="country"
            value={form.country}
            onChange={handleChange}
            className={errors.country ? 'error' : ''}
          />
          {errors.country && <span className="error-text">{errors.country}</span>}
        </div>

        <div className="form-group">
          <label>State</label>
          <input
            type="text"
            name="state"
            value={form.state}
            onChange={handleChange}
            className={errors.state ? 'error' : ''}
          />
          {errors.state && <span className="error-text">{errors.state}</span>}
        </div>

        <div className="form-group">
          <label>City</label>
          <input
            type="text"
            name="city"
            value={form.city}
            onChange={handleChange}
            className={errors.city ? 'error' : ''}
          />
          {errors.city && <span className="error-text">{errors.city}</span>}
        </div>
      </div>

      <div className="form-row">
        <div className="form-group">
          <label>Pincode</label>
          <input
            type="text"
            name="pincode"
            value={form.pincode}
            onChange={handleChange}
            className={errors.pincode ? 'error' : ''}
            maxLength="6"
          />
          {errors.pincode && <span className="error-text">{errors.pincode}</span>}
        </div>

        <div className="form-group">
          <label>Gender</label>
          <select
            name="gender"
            value={form.gender}
            onChange={handleChange}
            className={errors.gender ? 'error' : ''}
          >
            <option value="">Select</option>
            <option value="Male">Male</option>
            <option value="Female">Female</option>
            <option value="Other">Other</option>
          </select>
          {errors.gender && <span className="error-text">{errors.gender}</span>}
        </div>
      </div>

      <div className="form-group">
        <label>Address</label>
        <textarea
          name="address"
          value={form.address}
          onChange={handleChange}
          className={errors.address ? 'error' : ''}
          rows="3"
        />
        {errors.address && <span className="error-text">{errors.address}</span>}
      </div>

      <div className="form-actions">
        <button type="submit" className="btn-submit">
          {editing ? 'Update' : 'Save'}
        </button>
        <button type="button" onClick={onCancel} className="btn-cancel">
          Cancel
        </button>
      </div>
    </form>
  );
};

export default EmployeeForm;
