import React, { useEffect, useState } from 'react';
import api from '../services/api';

const emptyForm = {
  firstName: '',
  lastName: '',
  email: '',
  mobile: '',
  country: '',
  state: '',
  city: '',
  pincode: '',
  address: '',
  gender: '',
  image: null,
  resume: null,
};

const allowAlphabets = (value) => value.replace(/[^A-Za-z\s]/g, '');
const capitalize = (str) => str.charAt(0).toUpperCase() + str.slice(1);

const EmployeeForm = ({ editing, onSaved, onCancel }) => {
  const [form, setForm] = useState(emptyForm);
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (editing) setForm(editing);
    else setForm(emptyForm);
  }, [editing]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === 'image' || name === 'resume') {
      setForm((prev) => ({ ...prev, [name]: files[0] }));
      return;
    }

    let newValue = value;
    if (name === 'firstName' || name === 'lastName') {
      newValue = capitalize(allowAlphabets(value));
    }
    if (name === 'mobile' || name === 'pincode') {
      newValue = value.replace(/[^0-9]/g, '');
    }

    setForm((prev) => ({ ...prev, [name]: newValue }));
    setErrors((prev) => ({ ...prev, [name]: '' }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!form.firstName || form.firstName.trim().length < 2)
      newErrors.firstName = 'First name must be at least 2 characters';
    if (!form.lastName || form.lastName.trim().length < 2)
      newErrors.lastName = 'Last name must be at least 2 characters';
    if (!form.email) newErrors.email = 'Email is required';
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(form.email))
      newErrors.email = 'Please enter a valid email';
    if (!form.mobile) newErrors.mobile = 'Mobile is required';
    else if (!/^\d{10}$/.test(form.mobile))
      newErrors.mobile = 'Mobile must be 10 digits';
    if (!form.country) newErrors.country = 'Country is required';
    if (!form.state) newErrors.state = 'State is required';
    if (!form.city) newErrors.city = 'City is required';
    if (!form.pincode) newErrors.pincode = 'Pincode is required';
    else if (!/^\d{6}$/.test(form.pincode))
      newErrors.pincode = 'Pincode must be 6 digits';
    if (!form.address) newErrors.address = 'Address is required';
    else if (form.address.trim().length < 10)
      newErrors.address = 'Address must be at least 10 characters';
    if (!form.gender) newErrors.gender = 'Select gender';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    setLoading(true);

    try {
      const formData = new FormData();
      Object.keys(form).forEach((key) => {
        if (form[key]) formData.append(key, form[key]);
      });

      if (editing && editing._id) {
        await api.put(`/${editing._id}`, formData);
        alert('Employee updated successfully!');
      } else {
        await api.post('/', formData);
        alert('Employee registered successfully!');
      }

      setForm(emptyForm);
      onSaved && onSaved();
    } catch (err) {
      const msg = err?.response?.data?.message || err.message;
      alert(`Error: ${msg}`);
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setForm(emptyForm);
    setErrors({});
  };

  const handleCancel = () => {
    handleClear();
    onCancel && onCancel();
  };

  return (
    <div className="card p-4 shadow-sm mb-3">
      <h5 className="mb-3">{editing ? 'Edit Employee' : 'Register Employee'}</h5>

      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="col-md-6 mb-3">
            <label>Profile Image</label>
            <input
              type="file"
              name="image"
              onChange={handleChange}
              className={`form-control ${errors.image ? 'is-invalid' : ''}`}
            />
            {errors.image && <div className="invalid-feedback">{errors.image}</div>}
          </div>

          <div className="col-md-6 mb-3">
            <label>Resume</label>
            <input
              type="file"
              name="resume"
              onChange={handleChange}
              className={`form-control ${errors.resume ? 'is-invalid' : ''}`}
            />
            {errors.resume && <div className="invalid-feedback">{errors.resume}</div>}
          </div>

          <div className="col-md-6 mb-3">
            <label>First Name</label>
            <input
              name="firstName"
              value={form.firstName}
              onChange={handleChange}
              placeholder="Enter First Name"
              className={`form-control ${errors.firstName ? 'is-invalid' : ''}`}
            />
            {errors.firstName && <div className="invalid-feedback">{errors.firstName}</div>}
          </div>

          <div className="col-md-6 mb-3">
            <label>Last Name</label>
            <input
              name="lastName"
              value={form.lastName}
              onChange={handleChange}
              placeholder="Enter Last Name"
              className={`form-control ${errors.lastName ? 'is-invalid' : ''}`}
            />
            {errors.lastName && <div className="invalid-feedback">{errors.lastName}</div>}
          </div>
        </div>

        {/* Email, Mobile, Location, etc. */}
        {/* Keep rest of your logic same as before */}

        <div className="d-flex gap-2">
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Please wait...' : editing ? 'Update Employee' : 'Save Employee'}
          </button>

          <button
            type="button"
            className="btn btn-warning"
            onClick={handleClear}
            disabled={loading}
          >
            Clear Form
          </button>

          {editing && (
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleCancel}
              disabled={loading}
            >
              Cancel
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default EmployeeForm;
